import React, { Component, ErrorInfo, ReactNode } from 'react';

interface Props {
  children: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
}

class ErrorBoundary extends Component<Props, State> {
  public state: State = {
    hasError: false
  };

  public static getDerivedStateFromError(error: Error): State {
    // Check if it's a WalletConnect session error
    if (error.message?.includes('No matching key. session topic doesn\'t exist') ||
        error.message?.includes('Pending session not found') ||
        error.message?.includes('No matching key. proposal')) {
      // Don't show error boundary for WalletConnect session errors
      return { hasError: false };
    }

    // Update state so the next render will show the fallback UI
    return { hasError: true, error };
  }

  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Check if it's a WalletConnect session error
    if (error.message?.includes('No matching key. session topic doesn\'t exist') ||
        error.message?.includes('Pending session not found') ||
        error.message?.includes('No matching key. proposal')) {
      // Log but don't show error boundary for WalletConnect session errors
      console.log('WalletConnect session error caught by ErrorBoundary:', error.message);
      return;
    }

    console.error('Uncaught error:', error, errorInfo);
  }

  public render() {
    if (this.state.hasError) {
      return (
        <div style={{ 
          padding: '20px', 
          textAlign: 'center', 
          color: '#ff4444',
          backgroundColor: '#ffe6e6',
          border: '1px solid #ff4444',
          borderRadius: '8px',
          margin: '20px'
        }}>
          <h2>Something went wrong.</h2>
          <p>Please refresh the page and try again.</p>
          <button 
            onClick={() => window.location.reload()}
            style={{
              padding: '10px 20px',
              backgroundColor: '#ff4444',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Refresh Page
          </button>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
