export const PINO_LOGGER_DEFAULTS = {
  level: "info",
};

export const PINO_CUSTOM_CONTEXT_KEY = "custom_context";

export const MAX_LOG_SIZE_IN_BYTES_DEFAULT = 1000 * 1024; // 1MB
class LogListNode {
  private nodeValue: string;
  private sizeInBytes: number;
  public next: LogListNode | null;

  constructor(value: string) {
    this.nodeValue = value;
    this.sizeInBytes = new TextEncoder().encode(this.nodeValue).length;
    this.next = null;
  }

  public get value() {
    return this.nodeValue;
  }

  public get size() {
    return this.sizeInBytes;
  }
}

export default class LogLinkedList {
  private lengthInNodes: number;
  private sizeInBytes: number;
  private head: LogListNode | null;
  private tail: LogListNode | null;
  private maxSizeInBytes: number;

  constructor(maxSizeInBytes: number) {
    this.head = null;
    this.tail = null;
    this.lengthInNodes = 0;
    this.maxSizeInBytes = maxSizeInBytes;
    this.sizeInBytes = 0;
  }

  public append(value: string): void {
    const newNode = new LogListNode(value);

    if (newNode.size > this.maxSizeInBytes) {
      throw new Error(
        `[LinkedList] Value too big to insert into list: ${value} with size ${newNode.size}`,
      );
    }

    while (this.size + newNode.size > this.maxSizeInBytes) {
      this.shift();
    }

    if (!this.head) {
      this.head = newNode;
      this.tail = newNode;
    } else {
      if (this.tail) {
        this.tail.next = newNode;
      }
      this.tail = newNode;
    }
    this.lengthInNodes++;
    this.sizeInBytes += newNode.size;
  }

  public shift(): void {
    if (!this.head) {
      return;
    }

    const removedNode = this.head;
    this.head = this.head.next;

    if (!this.head) {
      this.tail = null;
    }

    this.lengthInNodes--;
    this.sizeInBytes -= removedNode.size;
  }

  public toArray(): string[] {
    const array: string[] = [];
    let currentNode = this.head;
    while (currentNode !== null) {
      array.push(currentNode.value);
      currentNode = currentNode.next;
    }
    return array;
  }

  public get length() {
    return this.lengthInNodes;
  }

  public get size() {
    return this.sizeInBytes;
  }

  public toOrderedArray() {
    return Array.from(this);
  }

  [Symbol.iterator](): Iterator<string> {
    let node = this.head;

    const next = (): IteratorResult<string> => {
      if (!node) {
        return { done: true, value: null };
      }

      const value = node.value;
      node = node.next;

      return { done: false, value };
    };

    return { next };
  }
}
import { MAX_LOG_SIZE_IN_BYTES_DEFAULT } from "./constants";
import type { LoggerOptions } from "pino";
import { levels } from "pino";
import LinkedList from "./linkedList";
import { safeJsonStringify } from "@walletconnect/safe-json";

export default class BaseChunkLogger {
  private logs: LinkedList;
  private level: LoggerOptions["level"];
  private levelValue: number;

  private MAX_LOG_SIZE_IN_BYTES: number;

  public constructor(
    level: LoggerOptions["level"],
    MAX_LOG_SIZE_IN_BYTES: number = MAX_LOG_SIZE_IN_BYTES_DEFAULT,
  ) {
    this.level = level ?? "error";
    this.levelValue = levels.values[this.level];

    this.MAX_LOG_SIZE_IN_BYTES = MAX_LOG_SIZE_IN_BYTES;
    this.logs = new LinkedList(this.MAX_LOG_SIZE_IN_BYTES);
  }

  public forwardToConsole(chunk: any, level: number) {
    if (level === levels.values.error) {
      // eslint-disable-next-line no-console
      console.error(chunk);
    } else if (level === levels.values.warn) {
      // eslint-disable-next-line no-console
      console.warn(chunk);
    } else if (level === levels.values.debug) {
      // eslint-disable-next-line no-console
      console.debug(chunk);
    } else if (level === levels.values.trace) {
      // eslint-disable-next-line no-console
      console.trace(chunk);
    } else {
      // eslint-disable-next-line no-console
      console.log(chunk);
    }
  }

  public appendToLogs(chunk: any) {
    this.logs.append(
      safeJsonStringify({
        timestamp: new Date().toISOString(),
        log: chunk,
      }),
    );

    // Based on https://github.com/pinojs/pino/blob/master/lib/constants.js
    const level = typeof chunk === "string" ? JSON.parse(chunk).level : chunk.level;
    if (level >= this.levelValue) {
      this.forwardToConsole(chunk, level);
    }
  }

  public getLogs() {
    return this.logs;
  }

  public clearLogs() {
    this.logs = new LinkedList(this.MAX_LOG_SIZE_IN_BYTES);
  }

  public getLogArray() {
    return Array.from(this.logs);
  }

  public logsToBlob(extraMetadata: Record<string, string>) {
    const logArray = this.getLogArray();
    logArray.push(safeJsonStringify({ extraMetadata }));
    const blob = new Blob(logArray, { type: "application/json" });
    return blob;
  }
}
import { MAX_LOG_SIZE_IN_BYTES_DEFAULT } from "./constants";
import type { LoggerOptions } from "pino";
import BaseChunkLogger from "./baseChunkLogger";

export default class ClientChunkLogger {
  private baseChunkLogger: BaseChunkLogger;

  public constructor(
    level: LoggerOptions["level"],
    MAX_LOG_SIZE_IN_BYTES: number = MAX_LOG_SIZE_IN_BYTES_DEFAULT,
  ) {
    this.baseChunkLogger = new BaseChunkLogger(level, MAX_LOG_SIZE_IN_BYTES);
  }

  public write(chunk: any): void {
    this.baseChunkLogger.appendToLogs(chunk);
  }

  public getLogs() {
    return this.baseChunkLogger.getLogs();
  }

  public clearLogs() {
    this.baseChunkLogger.clearLogs();
  }

  public getLogArray() {
    return this.baseChunkLogger.getLogArray();
  }

  public logsToBlob(extraMetadata: Record<string, string>) {
    return this.baseChunkLogger.logsToBlob(extraMetadata);
  }

  public downloadLogsBlobInBrowser(extraMetadata: Record<string, string>) {
    const url = URL.createObjectURL(this.logsToBlob(extraMetadata));
    const anchor = document.createElement("a");
    anchor.href = url;
    anchor.download = `walletconnect-logs-${new Date().toISOString()}.txt`;
    document.body.appendChild(anchor);
    anchor.click();
    document.body.removeChild(anchor);
    URL.revokeObjectURL(url);
  }
}
