import React, { useEffect, useState } from 'react';
import { useWallet } from '../contexts/WalletContext';
import './NetworkChecker.css';

const AMOY_CHAIN_ID = 80002;
const AMOY_CHAIN_ID_HEX = '0x13882';

const NetworkChecker: React.FC = () => {
  const { chainId, account, switchNetwork, walletType } = useWallet();
  const [showBanner, setShowBanner] = useState(false);
  const [isSwitching, setIsSwitching] = useState(false);

  useEffect(() => {
    if (account && chainId && chainId !== AMOY_CHAIN_ID) {
      setShowBanner(true);
    } else {
      setShowBanner(false);
    }
  }, [chainId, account]);

  const switchToAmoy = async () => {
    setIsSwitching(true);
    try {
      await switchNetwork(AMOY_CHAIN_ID);
    } catch (error: any) {
      console.error('Error switching network:', error);

      let errorMessage = 'Failed to switch network. ';
      if (walletType === 'walletconnect') {
        errorMessage += 'Please switch to Polygon Amoy manually in your wallet app.';
      } else {
        errorMessage += 'Please switch manually in MetaMask.';
      }

      alert(errorMessage);
    } finally {
      setIsSwitching(false);
    }
  };

  if (!showBanner) {
    return null;
  }

  return (
    <div className="network-banner">
      <div className="network-banner-content">
        <span className="warning-icon">⚠️</span>
        <div className="banner-text">
          <strong>Wrong Network!</strong>
          <p>
            You're on {chainId ? `Chain ID ${chainId}` : 'wrong network'} using {walletType || 'wallet'}. 
            This marketplace requires <strong>Polygon Amoy (Chain ID: 80002)</strong>
          </p>
        </div>
        <button 
          className="switch-button" 
          onClick={switchToAmoy}
          disabled={isSwitching}
        >
          {isSwitching ? 'Switching...' : 'Switch to Amoy'}
        </button>
      </div>
    </div>
  );
};

export default NetworkChecker;
