import { useMemo } from 'react';
import { ethers } from 'ethers';
import { useWallet } from '../contexts/WalletContext';
import { MarketplaceSDK } from '../sdk/MarketplaceSDK';

const MARKETPLACE_ADDRESS = process.env.MARKETPLACE_ADDRESS || '';
// Support both naming variants found in env/example
const NFT_ADDRESS =
  process.env.STR_DOMAIN_NFT_COLLECTION ||
  process.env.NFT_COLLECTION_ADDRESS ||
  '';
const IS_DEV = process.env.NODE_ENV === 'development';



/**
 * Custom hook to access the Marketplace SDK
 * Returns null if wallet is not connected
 */
export const useMarketplaceSDK = (): MarketplaceSDK | null => {
  const { signer } = useWallet();
console.log(MARKETPLACE_ADDRESS,NFT_ADDRESS)

  const sdk = useMemo(() => {
    if (!MARKETPLACE_ADDRESS || !NFT_ADDRESS) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Missing marketplace/NFT env addresses');
      }
      return null;
    }

    try {
      if (signer) {
        return new MarketplaceSDK(signer, MARKETPLACE_ADDRESS, NFT_ADDRESS, IS_DEV);
      }

      // Read-only fallback using RPC URL if available
      const rpcUrl = process.env.REACT_APP_RPC_URL;
      if (rpcUrl) {
        const provider = new ethers.JsonRpcProvider(rpcUrl);
        // Create a dummy signer bound to provider (read-only methods will still work using provider via signer.provider)
        const dummySigner = (provider as any).getSigner?.() || ethers.Wallet.createRandom().connect(provider);
        return new MarketplaceSDK(dummySigner, MARKETPLACE_ADDRESS, NFT_ADDRESS, IS_DEV);
      }

      return null;
    } catch (error) {
      console.error('Error creating Marketplace SDK:', error);
      return null;
    }
  }, [signer]);

  return sdk;
};

export default useMarketplaceSDK;
